    sendHashIPFSEthContract(hashIPFS){

      var address = '0x23b0c08603e42eBC38D395dbE6E6E834f8Cd67F6';
      var key = '545f780fec7a2702724f4a5499c2045f2c97ad54d3a051f8994044b2d4196835';

      var txOptions = {
      nonce: web3.toHex(web3.eth.getTransactionCount(address)),
      gasLimit: web3.toHex(800000),
      gasPrice: web3.toHex(200000000000),
      to: this._contractAdressContract
      }

      var abi = [
        {
          "constant": true,
          "inputs": [],
          "name": "getHash",
          "outputs": [
            {
              "name": "x",
              "type": "string"
            }
          ],
          "payable": false,
          "stateMutability": "view",
          "type": "function"
        },
        {
          "constant": false,
          "inputs": [
            {
              "name": "x",
              "type": "string"
            }
          ],
          "name": "sendHash",
          "outputs": [],
          "payable": false,
          "stateMutability": "nonpayable",
          "type": "function"
        }
      ];
      
      function sendRaw(rawTx) {
        var privateKey = new Buffer(key, 'hex');
        var transaction = new tx(rawTx);
        transaction.sign(privateKey);
        var serializedTx = transaction.serialize().toString('hex');
        web3.eth.sendRawTransaction(
        '0x' + serializedTx, function(err, result) {
            if(err) {
                console.log(err);
            } else {
                console.log(result)
            }
        });
    }

    console.log('New Ethereum transaction with IPFS Hash (that contain`s synced database of contracts):')
      
    var rawTx = txutils.functionTx(abi, 'sendHash', [hashIPFS], txOptions);

    sendRaw(rawTx);
    }
